PREFIX dcm: <http://semantic-dicom.org/dcm#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
SELECT ?seriesInstanceUID ?seriesDescription ?avgSliceThickness ?stdDevSliceThickness
WHERE {
  {
    SELECT ?seriesInstanceUID ?seriesDescription (AVG(xsd:float(?sliceThickness)) AS ?avgSliceThickness) (STDEV(xsd:float(?sliceThickness)) AS ?stdDevSliceThickness) (COUNT(?image) AS ?imageCount)
    WHERE {
      ?series rdf:type dcm:Series .
      ?series dcm:SeriesInstanceUID ?seriesInstanceUID .
      OPTIONAL { ?series dcm:SeriesDescription ?seriesDescription . }
      ?series dcm:containsImage ?image .
      ?image dcm:SliceThickness ?sliceThickness .
    }
    GROUP BY ?seriesInstanceUID ?seriesDescription
  }
  FILTER(?imageCount > 5)
}
ORDER BY ?stdDevSliceThickness
LIMIT 10

PREFIX dcm: <http://semantic-dicom.org/dcm#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
