SELECT ?seriesInstanceUID ?seriesDescription ?avgPixelSpacing ?stdDevPixelSpacing
WHERE {
  {
    SELECT ?seriesInstanceUID ?seriesDescription (AVG(xsd:float(STRBEFORE(?pixelSpacing, "\\"))) AS ?avgPixelSpacing) (STDEV(xsd:float(STRBEFORE(?pixelSpacing, "\\"))) AS ?stdDevPixelSpacing) (COUNT(?image) AS ?imageCount)
    WHERE {
      ?series rdf:type dcm:Series .
      ?series dcm:SeriesInstanceUID ?seriesInstanceUID .
      OPTIONAL { ?series dcm:SeriesDescription ?seriesDescription . }
      ?series dcm:containsImage ?image .
      ?image dcm:PixelSpacing ?pixelSpacing .
    }
    GROUP BY ?seriesInstanceUID ?seriesDescription
  }
  FILTER(?imageCount > 5)
}
ORDER BY ?stdDevPixelSpacing
LIMIT 10

PREFIX dcm: <http://semantic-dicom.org/dcm#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
SELECT ?patientName ?avgInterval ?stdDevInterval
WHERE {
  {
    SELECT ?patientName (AVG(?daysBetween) AS ?avgInterval) (STDEV(?daysBetween) AS ?stdDevInterval) (COUNT(?daysBetween) AS ?intervalCount)
    WHERE {
      SELECT ?patientName ?study1 ?study2 (xsd:date(?studyDate2) - xsd:date(?studyDate1) AS ?daysBetween)
      WHERE {
        ?patient rdf:type dcm:Patient .
        ?patient dcm:PatientName ?patientName .
        ?study1 rdf:type dcm:Study .
        ?study1 dcm:isStudyOf ?patient .
        ?study1 dcm:StudyDate ?studyDate1 .
        ?study2 rdf:type dcm:Study .
        ?study2 dcm:isStudyOf ?patient .
        ?study2 dcm:StudyDate ?studyDate2 .
        FILTER(?study1 != ?study2 && ?studyDate1 < ?studyDate2)
        FILTER NOT EXISTS {
          ?study3 rdf:type dcm:Study .
          ?study3 dcm:isStudyOf ?patient .
          ?study3 dcm:StudyDate ?studyDate3 .
          FILTER(?studyDate1 < ?studyDate3 && ?studyDate3 < ?studyDate2)
        }
      }
    }
    GROUP BY ?patientName
  }
  FILTER(?intervalCount >= 3)
}
ORDER BY ?stdDevInterval
LIMIT 10

PREFIX dcm: <http://semantic-dicom.org/dcm#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT ?studyInstanceUID ?studyDescription ?avgImagesPerSeries ?stdDevImagesPerSeries ?seriesCount
WHERE {
  {
    SELECT ?studyInstanceUID ?studyDescription (AVG(?imageCount) AS ?avgImagesPerSeries) (STDEV(?imageCount) AS ?stdDevImagesPerSeries) (COUNT(?series) AS ?seriesCount)
    WHERE {
      SELECT ?studyInstanceUID ?studyDescription ?series (COUNT(?image) AS ?imageCount)
      WHERE {
        ?study rdf:type dcm:Study .
        ?study dcm:StudyInstanceUID ?studyInstanceUID .
        OPTIONAL { ?study dcm:StudyDescription ?studyDescription . }
        ?series rdf:type dcm:Series .
        ?series dcm:isSeriesOf ?study .
        ?series dcm:containsImage ?image .
      }
      GROUP BY ?studyInstanceUID ?studyDescription ?series
    }
    GROUP BY ?studyInstanceUID ?studyDescription
  }
  FILTER(?seriesCount >= 3)
}
ORDER BY ?stdDevImagesPerSeries
LIMIT 10

