PREFIX dcm: <http://semantic-dicom.org/dcm#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT (AVG(?countPerMonth) AS ?avgStudiesPerMonth)
WHERE {
  {
    SELECT ?month (COUNT(DISTINCT ?study) AS ?countPerMonth)
    WHERE {
      ?study rdf:type dcm:Study .
      ?study dcm:StudyDate ?studyDate .
      ?series dcm:isSeriesOf ?study .
      ?series dcm:Modality "CT" .
      FILTER(CONTAINS(STR(?studyDate), "2023"))
      BIND(SUBSTR(?studyDate, 6, 2) AS ?month)
    }
    GROUP BY ?month
  }
}

PREFIX dcm: <http://semantic-dicom.org/dcm#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT ?dayOfWeek (COUNT(?study) AS ?studyCount)
WHERE {
  ?study rdf:type dcm:Study .
  ?study dcm:StudyDate ?studyDate .
  ?series dcm:isSeriesOf ?study .
  ?series dcm:Modality "CT" .
  BIND(
    IF(CONTAINS(STR(?studyDate), "Monday"), "Monday",
      IF(CONTAINS(STR(?studyDate), "Tuesday"), "Tuesday",
        IF(CONTAINS(STR(?studyDate), "Wednesday"), "Wednesday",
          IF(CONTAINS(STR(?studyDate), "Thursday"), "Thursday",
            IF(CONTAINS(STR(?studyDate), "Friday"), "Friday",
              IF(CONTAINS(STR(?studyDate), "Saturday"), "Saturday", "Sunday")
            )
          )
        )
      )
    ) AS ?dayOfWeek
  )
}
GROUP BY ?dayOfWeek
ORDER BY DESC(?studyCount)
LIMIT 1

PREFIX dcm: <http://semantic-dicom.org/dcm#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
SELECT ?series (AVG(?secondsBetween) AS ?avgSecondsBetween)
WHERE {
  {
    SELECT ?series ?acquisitionTime1 ?acquisitionTime2 
           (xsd:integer(xsd:time(?acquisitionTime2)) - xsd:integer(xsd:time(?acquisitionTime1)) AS ?secondsBetween)
    WHERE {
      ?series rdf:type dcm:Series .
      ?series dcm:Modality "CT" .
      ?image1 dcm:isImageOf ?series .
      ?image1 dcm:AcquisitionTime ?acquisitionTime1 .
      ?image2 dcm:isImageOf ?series .
      ?image2 dcm:AcquisitionTime ?acquisitionTime2 .
      FILTER(xsd:time(?acquisitionTime1) < xsd:time(?acquisitionTime2))
      FILTER NOT EXISTS {
        ?image3 dcm:isImageOf ?series .
        ?image3 dcm:AcquisitionTime ?acquisitionTime3 .
        FILTER(xsd:time(?acquisitionTime1) < xsd:time(?acquisitionTime3) && xsd:time(?acquisitionTime3) < xsd:time(?acquisitionTime2))
      }
    }
  }
}
GROUP BY ?series

